import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Checkbox, Button, InputNumber, Typography, Row, Col, Divider, Layout, Image } from "antd";
import { DeleteOutlined } from '@ant-design/icons';

import { MainLayout } from "./MainLayout";
import { addProductToCart, getCart, removeProductFromCart, updateProductQuantityInCart } from "../apis";

const { Header, Content } = Layout;
const { Title, Text } = Typography;

const headerStyle = {
  textAlign: 'center',
  color: '#fff',
  height: 64,
  paddingInline: 48,
  lineHeight: '64px',
  backgroundColor: '#4096ff',
  fontSize: 32,
  marginBottom: 12
};

export default function Cart () {
  const [cartItems, setCartItems] = useState([]);

  const { items, status, error } = useSelector((state) => state.cart)
  const dispatch = useDispatch()

  useEffect(() => {
    const fetchCartData = async () => {
      const cartData = await dispatch(getCart('507f191e810c19729de860ea'));
      // Assuming `cartData.payload` contains the products array
      console.log('cartData: ', cartData?.payload?.products)
      if (cartData && cartData?.payload) {
        setCartItems(cartData?.payload?.products);
      }
    };
    fetchCartData();
  }, [dispatch]);

  const handleQuantityChange = (value, id) => {
    setCartItems(prevItems =>
      prevItems.map(item =>
        item.productId._id === id ? { ...item, quantity: value } : item
      )
    );
    const data = {
      userId: '507f191e810c19729de860ea',
      productId: id,
      quantity: value
    }
    const resHandleQuantityChange = dispatch(updateProductQuantityInCart(data))
    console.log('handleAddToCartData: ', resHandleQuantityChange)
  };

  const handleDelete = async (productId) => {
    console.log('items before delete', items)
    setCartItems(prevItems => prevItems.filter(item => item.productId._id !== productId));
    await dispatch(removeProductFromCart({
      userId: '507f191e810c19729de860ea',  // Correct userId here
      productId
    }));
    console.log('items after delete', items)
  };

  const handleSelect = (e, id) => {
    setCartItems(prevItems =>
      prevItems.map(item =>
        item.productId._id === id ? { ...item, selected: e.target.checked } : item
      )
    );
  };

  const selectedItems = cartItems.filter(item => item.selected);
  console.log('selectedItems: ', selectedItems)
  const totalAmount = selectedItems.reduce(
    (sum, item) => sum + item.productId.price * item.quantity,
    0
  );

  return (
    <>
      <MainLayout>
        <Header style={headerStyle}>Product List</Header>
        <div style={{ display: "flex", padding: "20px" }}>
        {/* Cart Items Section */}
        <div style={{ flex: 1, paddingRight: "20px" }}>
          <h2>Giỏ hàng</h2>
          {cartItems.map(item => (
            <Row key={item.productId._id} align="middle" style={{ marginBottom: "20px" }}>
              <Col>
                <Checkbox
                  checked={item.selected || false}
                  onChange={e => handleSelect(e, item.productId._id)}
                />
              </Col>
              <Col flex="80px" style={{ paddingLeft: "10px" }}>
                <Image
                  width={60}
                  height={60}
                  src={item.productId.images[0]}
                  fallback="data:image/png;base64,"
                />
              </Col>
              <Col flex="auto" style={{ paddingLeft: "10px" }}>
                <div style={{ fontSize: "16px" }}>{item.productId.name}</div>
                <Text type="secondary">{item.productId.brand}</Text>
              </Col>
              <Col flex="120px" style={{ textAlign: "right" }}>
                <Text style={{ color: "green", fontSize: "16px" }}>
                  {item.productId.price.toLocaleString()} ₫
                </Text>
              </Col>
              <Col>
                <InputNumber
                  min={1}
                  value={item.quantity}
                  onChange={value => handleQuantityChange(value, item.productId._id)}
                />
              </Col>
              <Col>
                <Button
                  type="link"
                  onClick={() => handleDelete(item.productId._id)}
                  icon={<DeleteOutlined />}
                />
              </Col>
            </Row>
          ))}
        </div>

        {/* Selected Products Section */}
        <div style={{ flex: "0 0 300px", paddingLeft: "20px", borderLeft: "1px solid #f0f0f0" }}>
          <h2>Các sản phẩm đã chọn</h2>
          {selectedItems.map(item => (
            <Row key={item.productId._id} align="middle" style={{ marginBottom: "20px" }}>
              <Col flex="40px">
                <Image
                  width={40}
                  height={40}
                  src={item.productId.images[0]}
                  fallback="data:image/png;base64,"
                />
              </Col>
              <Col flex="auto" style={{ paddingLeft: "10px" }}>
                <div style={{ fontSize: "14px" }}>{item.productId.name}</div>
                <Text type="secondary">{item.productId.brand}</Text>
                <div>SL: {item.quantity}</div>
              </Col>
              <Col style={{ textAlign: "right" }}>
                <Text style={{ color: "green", fontSize: "14px" }}>
                  {item.productId.price.toLocaleString()} ₫
                </Text>
              </Col>
              <Col>
                <Button
                  type="link"
                  onClick={() => handleDelete(item.productId._id)}
                  icon={<DeleteOutlined />}
                />
              </Col>
            </Row>
          ))}
          <Divider />
          <Row>
            <Col flex="auto">
              <Text style={{ fontWeight: "bold" }}>Tổng số tiền:</Text>
            </Col>
            <Col>
              <Text style={{ color: "green", fontSize: "16px", fontWeight: "bold" }}>
                {totalAmount.toLocaleString()} ₫
              </Text>
            </Col>
          </Row>
        </div>
      </div>
      </MainLayout>
    </>
  )
}
